public static int calculateWeek(Date start, Date end) {
    Calendar cal = new GregorianCalendar();
    cal.setTime(start);

    int weeks = 0;
    while (cal.getTime().before(end)) {
        cal.add(Calendar.WEEK_OF_YEAR, 1);
        weeks++;
    }
    return weeks;
}
